// src/App.tsx

import React, { useState, useEffect, useCallback } from 'react';
import {
  Layout,
  Typography,
  Input,
  Button,
  Space,
  message,
  notification, // <-- IMPORTED
  ConfigProvider,
  theme,
} from 'antd';
import { PlusOutlined, ThunderboltFilled } from '@ant-design/icons';
import * as api from './services/api';
import { Task, NewTask, TaskExecution } from './types';
import TaskList from './components/TaskList';
import TaskForm from './components/TaskForm';
import RunResultModal from './components/RunResultModal';
import './App.css';

const { Header, Content, Footer } = Layout;
const { Title } = Typography;
const { Search } = Input;

// --- NEW HELPER FUNCTION ---
const updateTaskInList = (
  tasks: Task[],
  taskId: string,
  updates: Partial<Task>
) => {
  return tasks.map((t) => (t.id === taskId ? { ...t, ...updates } : t));
};

const App: React.FC = () => {
  // --- State ---
  const [tasks, setTasks] = useState<Task[]>([]);
  const [loading, setLoading] = useState(true);
  const [runningTaskIds, setRunningTaskIds] = useState<Set<string>>(new Set()); // <-- UPDATED

  // Form Modal State
  const [isFormModalVisible, setIsFormModalVisible] = useState(false);
  const [selectedTask, setSelectedTask] = useState<Task | null>(null);

  // Execution Result Modal State
  const [isExecModalVisible, setIsExecModalVisible] = useState(false);
  const [executionResult, setExecutionResult] = useState<TaskExecution | null>(null);

  // --- Data Fetching ---
  const fetchTasks = useCallback(async () => {
    setLoading(true);
    try {
      const data = await api.getTasks();
      setTasks(data);
    } catch (error) {
      notification.error({
        message: 'Failed to fetch tasks',
        description: (error as Error).message,
      });
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchTasks();
  }, [fetchTasks]);

  // --- Event Handlers ---
  const handleSearch = async (value: string) => {
    setLoading(true);
    try {
      const data = value ? await api.searchTasks(value) : await api.getTasks();
      setTasks(data);
    } catch (error) {
      message.error(`Search failed: ${(error as Error).message}`);
    } finally {
      setLoading(false);
    }
  };

  const handleSave = async (task: Task | NewTask) => {
    try {
      await api.saveTask(task);
      setIsFormModalVisible(false);
      fetchTasks(); // Refresh list
    } catch (error: any) {
      // The backend sends a 400 with a clear message
      const errorMsg = error.response?.data?.message || 'Failed to save task';
      notification.error({
        message: 'Save Failed',
        description: errorMsg,
      });
    }
  };

  const handleDelete = async (id: string) => {
    try {
      await api.deleteTask(id);
      message.success('Task deleted successfully');
      fetchTasks(); // Refresh list
    } catch (error) {
      notification.error({
        message: 'Failed to delete task',
        description: (error as Error).message,
      });
    }
  };

  // --- UPDATED handleRun ---
  const handleRun = async (task: Task) => {
    // 1. Set status to "Running" locally
    setRunningTaskIds((prev) => new Set(prev).add(task.id));
    const originalStatus = task.status; // Save original status

    try {
      const result = await api.runTask(task.id);
      
      // 2. Show modal and success notification
      setExecutionResult(result);
      setIsExecModalVisible(true);
      
      notification.success({
        message: `Task "${task.name}" Triggered`,
        description: `Successfully sent command to executor.`,
      });
      
      // 3. Refresh the full task list to get new execution history
      fetchTasks(); 

    } catch (error: any) {
      const errorMsg = error.response?.data?.message || 'Failed to run task';
      
      // 4. Show error notification
      notification.error({
        message: `Task "${task.name}" Failed`,
        description: errorMsg,
      });

      // 5. On error, revert status in UI
      setTasks((prevTasks) =>
        updateTaskInList(prevTasks, task.id, { status: originalStatus })
      );

    } finally {
      // 6. Remove from running set
      setRunningTaskIds((prev) => {
        const next = new Set(prev);
        next.delete(task.id);
        return next;
      });
    }
  };


  // --- Modal Triggers ---
  const showCreateModal = () => {
    setSelectedTask(null);
    setIsFormModalVisible(true);
  };

  const showEditModal = (task: Task) => {
    setSelectedTask(task);
    setIsFormModalVisible(true);
  };

  return (
    <ConfigProvider theme={{ algorithm: theme.darkAlgorithm }}>
      <Layout className="app-layout">
        <Header className="app-header">
          <Title level={2} style={{ color: 'white', margin: 0 }}>
            <ThunderboltFilled style={{ marginRight: 12 }} />
            Task Runner UI
          </Title>
        </Header>

        <Content className="app-content">
          <div className="content-container">
            <div className="toolbar">
              <Search
                placeholder="Search tasks by name..."
                onSearch={handleSearch}
                enterButton
                style={{ width: 400 }}
                aria-label="Search tasks by name"
              />
              <Button
                type="primary"
                icon={<PlusOutlined />}
                size="large"
                onClick={showCreateModal}
              >
                Create Task
              </Button>
            </div>

            <TaskList
              tasks={tasks}
              loading={loading}
              runningTaskIds={runningTaskIds} // <-- PASS UPDATED PROP
              onEdit={showEditModal}
              onDelete={handleDelete}
              onRun={handleRun} // <-- PASS UPDATED PROP
            />
          </div>
        </Content>

        <Footer style={{ textAlign: 'center' }}>
          Task Runner UI Â©2025 Created with React 19 & Ant Design
        </Footer>
      </Layout>

      {/* --- Modals --- */}
      <TaskForm
        visible={isFormModalVisible}
        onCancel={() => setIsFormModalVisible(false)}
        onSave={handleSave}
        initialData={selectedTask}
      />

      <RunResultModal
        visible={isExecModalVisible}
        onClose={() => setIsExecModalVisible(false)}
        execution={executionResult}
      />
    </ConfigProvider>
  );
};

export default App;
